package com.salesforce.pages;

import com.framework.selenium.api.design.Locators;
import com.framework.testng.api.base.ProjectSpecificMethods;

public class VerifyNewContact extends ProjectSpecificMethods {

	public VerifyNewContact verifyToastMeassage() {

		 contactName = getElementText(locateElement(Locators.XPATH, 
				"//span[contains(@class,'toastMessage slds-text-heading--small forceActionsText')]"));
		System.out.println(contactName);
		return this;
	}

	// Method to click the Account Name field (opens the dropdown)
	public VerifyNewContact clickAccountName() {
		click(locateElement(Locators.XPATH, "//input[@aria-controls='dropdown-element-360']"));
		reportStep("Clicked on Account Name field successfully.", "pass");
		return this;
	}

	// Method to select "New Account" from the dropdown
	public VerifyNewContact selectNewAccount() {
		click(locateElement(Locators.XPATH, "//lightning-base-combobox-item[contains(@data-value, 'actionCreateNew')]"));
		reportStep("Selected 'New Account' from the dropdown successfully.", "pass");
		return this;
	}

	// Method to click the Parent Account field (opens the dropdown)
	public VerifyNewContact clickParentAccountField() {
		// Locate the Parent Account input field by its ID or unique attribute
		click(locateElement(Locators.XPATH, "//input[@aria-controls='dropdown-element-1144']"));
		reportStep("Clicked on Parent Account field successfully.", "pass");
		return this;
	}

	// Method to select "Logeshwari" from the Parent Account dropdown list
	public VerifyNewContact selectLogeshwariFromList() {
		// Locate the dropdown item with the name "Logeshwari" and click it
		click(locateElement(Locators.XPATH, "//lightning-base-combobox-item//span[text()='Logeshwari']"));
		reportStep("Selected 'Logeshwari' from the Parent Account list successfully.", "pass");
		return this;
	}

	// Method to click the "Save" button
	public VerifyEditContactPage clickSaveButton() {
		click(locateElement(Locators.XPATH, "//button[@name='SaveEdit' and contains(text(), 'Save')]"));
		reportStep("Clicked on Save button successfully.", "pass");
		return new VerifyEditContactPage();
	}

	// Method to verify toast message after account creation
	public VerifyNewContact VerifyAccountCreatedToastMessage(String expectedAccountName) {
		// Locate the toast message element
		String toastMessage = getElementText(locateElement(Locators.XPATH,
				"//span[contains(@class, 'toastMessage slds-text-heading--small forceActionsText')]"));

		// Log the toast message for debugging
		System.out.println("Toast Message: " + toastMessage);

		// Verify if the expected account name is in the toast message
		if (toastMessage.contains(expectedAccountName)) {
			reportStep("Toast message verified successfully. Account '" + expectedAccountName + "' was created.", "pass");
		} else {
			reportStep("Toast message verification failed. Expected '" + expectedAccountName + "' but found '" + toastMessage + "'.", "fail");
		}

		return this;
	}

}

